import React, { useContext, useState } from "react";
import Slider from "react-slick";
import { useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import "../css/index.comp.css"; // Add your custom CSS here

const SingleProduct = () => {
  const { productId } = useParams();
  const { addToCart } = useContext(CartContext);
  const [quantity, setQuantity] = useState(1);

  // Example product data, replace with your actual data source
  const productData = [
    {
      id: 1,
      name: "Muscleblaze Biozyme Whey",
      description: "4.4lb Kesar Khulfi",
      price: 4699,
      images: [
        "https://img8.hkrtcdn.com/34083/prd_3408227-MuscleBlaze-Biozyme-Performance-Whey-4.4-lb-Kesar-Kulfi_c_m.jpg",
        "https://img6.hkrtcdn.com/34129/prd_3412835-MuscleBlaze-Biozyme-Performance-Whey-4.4-lb-Kesar-Kulfi_o.jpg",
        "https://img2.hkrtcdn.com/34129/prd_3412841_o.jpg",
      ],
    },
    {
      id: 2,
      name: "CreAmp Creatine Monohydrate",
      description: "0.88LB Unflavoured",
      price: 1399,
      images: [
        "https://img8.hkrtcdn.com/32169/prd_3216827-MuscleBlaze-Creatine-Monohydrate-0.88-lb-Unflavoured_c_m.jpg",
        "https://img8.hkrtcdn.com/32169/prd_3216817-MuscleBlaze-Creatine-Monohydrate-0.55-lb-Unflavoured_o.jpg",
        "https://img10.hkrtcdn.com/32169/prd_3216819-MuscleBlaze-Creatine-Monohydrate-0.55-lb-Unflavoured_o.jpg",
      ],
    },
    {
      id: 3,
      name: "Biozyme Daily Multivitamins",
      description: "90 tablets 5-in-1 Supplement",
      price: 899,
      images: [
        "https://img10.hkrtcdn.com/33253/prd_3325249_c_m.jpg",
        "https://img8.hkrtcdn.com/31845/prd_3184497-MuscleBlaze-Biozyme-Daily-Multivitamin-90-tablets-5in1-Supplement-with-Vitamins-Minerals-Joint-TBooster-Blend-EAF_o.jpg",
        "https://img6.hkrtcdn.com/31845/prd_3184495-MuscleBlaze-Biozyme-Daily-Multivitamin-90-tablets-5in1-Supplement-with-Vitamins-Minerals-Joint-TBooster-Blend-EAF_o.jpg",
      ],
    },
  ];

  const product = productData.find((p) => p.id === parseInt(productId));

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleQuantityChange = (change) => {
    setQuantity((prevQuantity) => Math.max(1, prevQuantity + change));
  };

  const handleAddToCart = () => {
    addToCart({ ...product, quantity });
  };

  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  return (
    <div className="single-product">
      <div className="image-slider">
        <Slider {...settings}>
          {product.images.map((image, index) => (
            <div key={index}>
              <img src={image} alt={product.name} />
            </div>
          ))}
        </Slider>
      </div>
      <div className="product-details">
        <h1>{product.name}</h1>
        <p>{product.description}</p>
        <p className="price">Price: {product.price}/-</p>
        <div className="quantity-control">
          <button onClick={() => handleQuantityChange(-1)}>-</button>
          <span>{quantity}</span>
          <button onClick={() => handleQuantityChange(1)}>+</button>
        </div>
        <button className="btn btn--green" onClick={handleAddToCart}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default SingleProduct;
