import React, { useContext, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import ImageSlider from "../components/ImageSlider";
import "../css/index.comp.css";

const SingleProduct = () => {
  const { id } = useParams();
  const { addToCart } = useContext(CartContext);
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    // Fetch the product details based on the ID
    // Replace the following line with your actual product fetch logic
    fetchProductDetails(id);
  }, [id]);

  const fetchProductDetails = async (productId) => {
    // Simulated fetch function
    const productData = await fetch(`/api/products/${productId}`).then((res) =>
      res.json()
    );
    setProduct(productData);
  };

  const handleAddToCart = () => {
    addToCart({ ...product, quantity });
  };

  const handleQuantityChange = (amount) => {
    if (quantity + amount > 0) {
      setQuantity(quantity + amount);
    }
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div className="single-product">
      <div className="single-product__images">
        <ImageSlider images={product.images} />
      </div>
      <div className="single-product__details">
        <h1 className="single-product__title">{product.title}</h1>
        <p className="single-product__price">${product.price}</p>
        <p className="single-product__description">{product.description}</p>
        <div className="single-product__quantity">
          <button
            onClick={() => handleQuantityChange(-1)}
            className="quantity-btn"
          >
            -
          </button>
          <span>{quantity}</span>
          <button
            onClick={() => handleQuantityChange(1)}
            className="quantity-btn"
          >
            +
          </button>
        </div>
        <button onClick={handleAddToCart} className="btn btn--green">
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default SingleProduct;
