import React from "react";
import { Link } from "react-router-dom";

const Card = ({ id, name, description, price, image, onDelete }) => {
  console.log("Card Props:", { id, name, description, price, image, onDelete });

  const handleDelete = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/products/${id}`, {
        method: "DELETE",
      });
      if (response.ok) {
        onDelete(id);
      } else {
        console.error("Failed to delete product");
      }
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  return (
    <div className="col-1-of-3">
      <div className="card">
        <div className="card__side card__side--front">
          <div
            className="card__picture card__picture--1"
            style={{ backgroundImage: `url(${image})` }}
          >
            &nbsp;
          </div>
          {/* <h4 className="card__heading">
            <span className="card__heading-span card__heading-span--1">
              {name}
            </span>
          </h4> */}
          <div className="card__details">
            <ul>
              <h3 className="heading-tertiary u-margin-bottom-small">{name}</h3>
              <li style={{ fontWeight: "bolder", color: "black" }}>
                {description}
              </li>
              <li style={{ fontWeight: "bold", color: "black" }}>
                Price: ₹{price}
              </li>
            </ul>
          </div>
        </div>
        <div className="card__side card__side--back card__side--back-1">
          <div className="card__cta">
            <div className="card__price-box">
              <p className="card__price-only">Only</p>
              <p className="card__price-value">₹{price}</p>
            </div>
            <Link to={`/product/${id}`} className="btn btn--white">
              SHOP NOW!
            </Link>
            <button onClick={handleDelete} className="btn btn--white">
              DELETE
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
