import React, { useContext, useState } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import "../css/index.comp.css"; // Add your custom CSS here

const SingleProduct = () => {
  const { productId } = useParams();
  const { addToCart } = useContext(CartContext);
  const [quantity, setQuantity] = useState(1);

  // Example product data, replace with your actual data source
  const productData = [
    {
      id: 1,
      name: "Muscleblaze Biozyme Whey",
      description: "4.4lb Kesar Khulfi",
      price: 4699,
      images: ["/img/nat-8.jpg", "/img/nat-9.jpg"],
    },
    {
      id: 2,
      name: "CreAmp Creatine Monohydrate",
      description: "0.88LB Unflavoured",
      price: 1399,
      images: ["/img/nat-10.jpg", "/img/nat-11.jpg"],
    },
    {
      id: 3,
      name: "Biozyme Daily Multivitamins",
      description: "90 tablets 5-in-1 Supplement",
      price: 899,
      images: ["/img/nat-12.jpg", "/img/nat-13.jpg"],
    },
  ];

  const product = productData.find((p) => p.id === parseInt(productId));

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleQuantityChange = (change) => {
    setQuantity((prevQuantity) => Math.max(1, prevQuantity + change));
  };

  const handleAddToCart = () => {
    addToCart({ ...product, quantity });
  };

  return (
    <div className="single-product container mx-auto my-8 p-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div className="image-slider">
          {product.images.map((image, index) => (
            <img
              key={index}
              src={image}
              alt={product.name}
              className="w-full h-auto mb-4"
            />
          ))}
        </div>
        <div className="product-details">
          <h1 className="text-3xl font-bold mb-4">{product.name}</h1>
          <p className="text-xl mb-4">{product.description}</p>
          <p className="text-2xl font-semibold mb-4">
            Price: {product.price}/-
          </p>
          <div className="quantity-control flex items-center space-x-2 mb-4">
            <button
              className="btn btn--green px-2"
              onClick={() => handleQuantityChange(-1)}
            >
              -
            </button>
            <span>{quantity}</span>
            <button
              className="btn btn--green px-2"
              onClick={() => handleQuantityChange(1)}
            >
              +
            </button>
          </div>
          <button className="btn btn--green" onClick={handleAddToCart}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default SingleProduct;
