import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/index.comp.css";

const ManageProducts = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: "",
    images: [],
  });

  useEffect(() => {
    const fetchProducts = async () => {
      const res = await axios.get("http://localhost:9000/api/admin/products");
      setProducts(res.data);
    };
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleAddProduct = async () => {
    try {
      const res = await axios.post(
        "http://localhost:9000/api/admin/products",
        newProduct
      );
      setProducts([...products, res.data]);
      setNewProduct({ name: "", description: "", price: "", images: [] });
    } catch (err) {
      console.error("Error adding product:", err);
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:9000/api/admin/products/${id}`);
      setProducts(products.filter((product) => product._id !== id));
    } catch (err) {
      console.error("Error deleting product:", err);
    }
  };

  return (
    <div className="manage-products">
      <h1>Manage Products</h1>
      <div className="add-product">
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="description"
          placeholder="Product Description"
          value={newProduct.description}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Product Price"
          value={newProduct.price}
          onChange={handleInputChange}
        />
        <button onClick={handleAddProduct} className="btn btn--green">
          Add Product
        </button>
      </div>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-item">
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <p>â‚¹{product.price}</p>
            <button
              onClick={() => handleDeleteProduct(product._id)}
              className="btn btn--red"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageProducts;
