import React, { useContext, useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import "../css/index.comp.css"; // Add your custom CSS here

const SingleProduct = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const { addToCart } = useContext(CartContext);
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const products = [
      {
        id: 1,
        name: "Muscleblaze Biozyme Whey",
        description: "4.4lb Kesar Khulfi",
        price: 4699,
        images: [
          "https://img8.hkrtcdn.com/34083/prd_3408227-MuscleBlaze-Biozyme-Performance-Whey-4.4-lb-Kesar-Kulfi_c_m.jpg",
          "https://img6.hkrtcdn.com/34129/prd_3412835-MuscleBlaze-Biozyme-Performance-Whey-4.4-lb-Kesar-Kulfi_o.jpg",
          "https://img2.hkrtcdn.com/34129/prd_3412841_o.jpg",
        ],
      },
      {
        id: 2,
        name: "CreAmp Creatine Monohydrate",
        description: "0.88LB Unflavoured",
        price: 1399,
        images: [
          "https://img8.hkrtcdn.com/32169/prd_3216827-MuscleBlaze-Creatine-Monohydrate-0.88-lb-Unflavoured_c_m.jpg",
          "https://img8.hkrtcdn.com/32169/prd_3216817-MuscleBlaze-Creatine-Monohydrate-0.55-lb-Unflavoured_o.jpg",
          "https://img10.hkrtcdn.com/32169/prd_3216819-MuscleBlaze-Creatine-Monohydrate-0.55-lb-Unflavoured_o.jpg",
        ],
      },
      {
        id: 3,
        name: "Biozyme Daily Multivitamins",
        description: "90 tablets 5-in-1 Supplement",
        price: 899,
        images: [
          "https://img10.hkrtcdn.com/33253/prd_3325249_c_m.jpg",
          "https://img8.hkrtcdn.com/31845/prd_3184497-MuscleBlaze-Biozyme-Daily-Multivitamin-90-tablets-5in1-Supplement-with-Vitamins-Minerals-Joint-TBooster-Blend-EAF_o.jpg",
          "https://img6.hkrtcdn.com/31845/prd_3184495-MuscleBlaze-Biozyme-Daily-Multivitamin-90-tablets-5in1-Supplement-with-Vitamins-Minerals-Joint-TBooster-Blend-EAF_o.jpg",
        ],
      },
    ];

    const selectedProduct = products.find((p) => p.id === parseInt(id));
    setProduct(selectedProduct);
  }, [id]);

  const handleAddToCart = () => {
    // const item = {
    //   id: product.id,
    //   name: product.name,
    //   description: product.description,
    //   price: product.price,
    //   image: product.images[0],
    // };
    addToCart({ ...product, quantity });
    navigate("/cart");
  };

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity > 0) {
      setQuantity(newQuantity);
      updateCartItem({ ...product, quantity: newQuantity });
    }
  };

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === product.images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const handlePreviousImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? product.images.length - 1 : prevIndex - 1
    );
  };

  if (!product) {
    return <p>Loading...</p>;
  }

  return (
    <>
      <div className="section-book section-book__singleProduct">
        <section className="section-about section-about__singleProduct">
          <div className="u-center-text u-margin-bottom-big">
            <h1 className="heading-secondary heading-secondary__singleProduct">
              Product Details
            </h1>
          </div>
          <div className="line"></div>
          <div className="row">
            <div className="col-1-of-2">
              <div className="single-product">
                <div className="single-product__images">
                  <button
                    onClick={handlePreviousImage}
                    className="btn btn--green slider-button slider-button--left"
                  >
                    &lt;
                  </button>
                  <img
                    src={product.images[currentImageIndex]}
                    alt={product.name}
                    className="single-product__image"
                  />
                  <button
                    onClick={handleNextImage}
                    className="btn btn--green slider-button slider-button--right"
                  >
                    &gt;
                  </button>
                </div>
              </div>
            </div>
            <div className="col-1-of-2">
              <div className="single-product__details">
                <h1 className="heading-tertiary u-margin-bottom-small heading-tertiary__singleProduct">
                  {product.name}
                </h1>
                <p className="heading-tertiary u-margin-bottom-small">
                  {product.description}
                </p>
                <p className="heading-tertiary u-margin-bottom-small single-product__price">
                  Price: ₹{product.price}
                </p>
                <div className="line"></div>
                <div className="quantity-control">
                  <button onClick={() => handleQuantityChange(quantity - 1)}>
                    -
                  </button>
                  <span>{quantity}</span>
                  <button onClick={() => handleQuantityChange(quantity + 1)}>
                    +
                  </button>
                </div>
                <button
                  onClick={handleAddToCart}
                  className="btn btn--green btn--addToCart"
                >
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
          <h1 className="heading-tertiary u-margin-bottom-small heading-tertiary--description">
            {" "}
            Product Description{" "}
          </h1>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Asperiores
            sit pariatur nisi eos quo mollitia totam quaerat dicta odio quisquam
            a architecto iste obcaecati tempore, excepturi sed voluptatem
            nesciunt similique. Sunt, quasi, eius qui deserunt soluta explicabo
            inventore magnam placeat fugit, nisi pariatur? Illum sit quidem esse
            dolor beatae consectetur ab, magni ipsum. Magni voluptatum, nisi
            commodi recusandae aspernatur itaque! Necessitatibus ut deserunt
            dicta eveniet ea et odio vel, blanditiis tempora architecto
            voluptates sequi, at inventore voluptatum similique? Quia, id ea
            recusandae accusantium pariatur vitae. Nisi animi consequuntur
            minima qui? At reiciendis provident quidem voluptas sapiente
            praesentium fugit itaque eligendi sed? Omnis dolore, voluptates
            incidunt eveniet repudiandae doloremque odio ipsam, amet odit,
            tempora autem molestias beatae necessitatibus natus blanditiis
            deleniti! Laboriosam odio quae commodi amet! A numquam quisquam
            praesentium? Velit nobis doloribus tempora laudantium nesciunt
            nostrum similique eos, soluta veniam quas iusto. Obcaecati neque
            asperiores at quam deleniti explicabo vel.
          </p>
        </section>
      </div>
    </>
  );
};

export default SingleProduct;
