import React, { useContext } from "react";
import { CartContext } from "../components/cartContext";
import "../css/cart.css";

const Cart = () => {
  const { cartItems, removeFromCart, updateCartItem } = useContext(CartContext);

  const handleDecreaseQuantity = (index, quantity) => {
    if (quantity > 1) {
      updateCartItem(index, quantity - 1);
    }
  };

  const handleIncreaseQuantity = (index, quantity) => {
    updateCartItem(index, quantity + 1);
  };

  return (
    <div className="cart">
      <h1 className="heading-secondary">Your Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item, index) => (
            <li key={index} className="cart-item">
              <img src={item.image} alt={item.name} />
              <div>
                <h2>{item.name}</h2>
                <p>{item.description}</p>
                <p className="price">Price: ${item.price}</p>
                <div className="quantity-control">
                  <button
                    onClick={() => handleDecreaseQuantity(index, item.quantity)}
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() => handleIncreaseQuantity(index, item.quantity)}
                  >
                    +
                  </button>
                </div>
              </div>
              <button
                className="remove-btn"
                onClick={() => removeFromCart(index)}
              >
                Remove
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
