import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/index.comp.css";

const ManageProducts = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    description: "",
    price: "",
    images: [],
    category: "Whey", // Default category
    sizes: [], // New attribute
    flavours: [], // New attribute
    longDescription: "", // New attribute
  });
  const [newImageUrl, setNewImageUrl] = useState("");
  const [newSize, setNewSize] = useState("");
  const [newFlavour, setNewFlavour] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      const res = await axios.get("http://localhost:9000/api/products");
      setProducts(res.data);
    };
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleImageUrlChange = (e) => {
    setNewImageUrl(e.target.value);
  };

  const handleAddImage = () => {
    if (newImageUrl.trim() !== "") {
      setNewProduct((prevState) => ({
        ...prevState,
        images: [...prevState.images, newImageUrl],
      }));
      setNewImageUrl("");
    }
  };

  const handleRemoveImage = (index) => {
    setNewProduct({
      ...newProduct,
      images: newProduct.images.filter((_, i) => i !== index),
    });
  };

  const handleAddProduct = async () => {
    try {
      // Ensure sizes and flavours are arrays
      const productToAdd = {
        ...newProduct,
        sizes: newProduct.sizes || [],
        flavours: newProduct.flavours || [],
      };
      const res = await axios.post(
        "http://localhost:9000/api/products",
        productToAdd
      );
      setProducts([...products, res.data]);
      setNewProduct({
        name: "",
        description: "",
        price: "",
        images: [],
        category: "Whey",
        sizes: [],
        flavours: [],
        longDescription: "", // Reset long description
      });
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddSize = () => {
    if (newSize.trim() !== "" && !newProduct.sizes.includes(newSize)) {
      setNewProduct((prevState) => ({
        ...prevState,
        sizes: [...prevState.sizes, newSize],
      }));
      setNewSize("");
    }
  };

  const handleAddFlavour = () => {
    if (newFlavour.trim() !== "" && !newProduct.flavours.includes(newFlavour)) {
      setNewProduct((prevState) => ({
        ...prevState,
        flavours: [...prevState.flavours, newFlavour],
      }));
      setNewFlavour("");
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:9000/api/products/${id}`);
      setProducts(products.filter((product) => product._id !== id));
    } catch (err) {
      console.error("Error deleting product:", err);
    }
  };

  return (
    <div className="manage-products">
      <h2>Manage Products</h2>
      <div className="product-form">
        <input
          type="text"
          name="name"
          placeholder="Product Name"
          value={newProduct.name}
          onChange={handleInputChange}
        />
        <input
          name="description"
          placeholder="Product Description"
          value={newProduct.description}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Product Price"
          value={newProduct.price}
          onChange={handleInputChange}
        />
        <textarea
          name="longDescription"
          placeholder="Product Long Description"
          value={newProduct.longDescription}
          onChange={handleInputChange}
        ></textarea>
        <div>
          <input
            type="text"
            placeholder="Image URL"
            value={newImageUrl}
            onChange={handleImageUrlChange}
          />
          <button onClick={handleAddImage}>Add Image</button>
          <div className="image-preview">
            {newProduct.images.map((image, index) => (
              <div key={index} className="image-item">
                <img src={image} alt={`Product Image ${index + 1}`} />
                <button onClick={() => handleRemoveImage(index)}>Remove</button>
              </div>
            ))}
          </div>
        </div>
        <select
          name="category"
          value={newProduct.category}
          onChange={handleInputChange}
        >
          <option value="Whey">Whey</option>
          <option value="Gainers">Gainers</option>
          <option value="Pre and Post Workouts">Pre and Post Workouts</option>
          <option value="Vitamins">Vitamins</option>
        </select>
        <div>
          <input
            type="text"
            placeholder="Size"
            value={newSize}
            onChange={(e) => setNewSize(e.target.value)}
          />
          <button onClick={handleAddSize}>Add Size</button>
          <div className="size-preview">
            {newProduct.sizes.map((size, index) => (
              <div key={index} className="size-item">
                <span>{size}</span>
              </div>
            ))}
          </div>
        </div>
        <div>
          <input
            type="text"
            placeholder="Flavour"
            value={newFlavour}
            onChange={(e) => setNewFlavour(e.target.value)}
          />
          <button onClick={handleAddFlavour}>Add Flavour</button>
          <div className="flavour-preview">
            {newProduct.flavours.map((flavour, index) => (
              <div key={index} className="flavour-item">
                <span>{flavour}</span>
              </div>
            ))}
          </div>
        </div>
        <button onClick={handleAddProduct}>Add Product</button>
      </div>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-item">
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>Price: ₹{product.price}</p>
            <div className="image-gallery">
              {product.images.map((image, index) => (
                <img
                  key={index}
                  src={image}
                  alt={`Product Image ${index + 1}`}
                />
              ))}
            </div>
            <p>Category: {product.category}</p>
            <p>Sizes: {product.sizes ? product.sizes.join(", ") : "N/A"}</p>
            <p>
              Flavours: {product.flavours ? product.flavours.join(", ") : "N/A"}
            </p>
            <p>Long Description: {product.longDescription || "N/A"}</p>
            <button
              onClick={() => handleDeleteProduct(product._id)}
              className="btn btn--green"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageProducts;
